@namespace url(http://www.w3.org/1999/xhtml);
body,
.modal-content {
    color: #bbb !important;
    background: #252525 !important
}
a.tg_checkbox {
    color: #bbb !important;
}
.im_page_wrap {
    /*background color of contentwrapper under header*/
    
    background-color: #303030 !important;
    border-left: 1px solid #4d4d4d !important;
    border-right: 1px solid #4d4d4d !important;
    border-bottom: 1px solid #4d4d4d !important;
    box-shadow: none;
}

.im_message_body {
    background: inherit !important;
}

.im_message_document_thumbed {
    background: inherit !important;
}

.im_dialog_peer {
    /*peer nickname unselected*/
    
    color: #bbb !important;
}
.contacts_modal_members_list a.contacts_modal_contact {
    /*nicknames in contactlist via hamburger menu*/
    
    color: #bbb;
}
.active a.im_dialog {
    /*selected peer */
    
    background-color: #4d4d4d !important;
}
.tg_head_split,
.md_modal_head {
    /*page header block*/
    
    background-color: #4d4d4d !important;
}
.dropdown.open .tg_head_btn,
.tg_head_btn:hover {
    background: #4d4d4d !important;
}
.form-control {
    /*input field*/
    background-color: #0e0e0e !important;
    color: #bbb !important;
}
.composer_rich_textarea {
        background: #303030 !important;
        border-color: #bbb !important;
}
.im_dialogs_col_wrap {
    /*contactlist right border*/
    
    border-right: 2px solid #4d4d4d;
}
.im_dialogs_col .nano > .nano-pane > .nano-slider {
    /*scrollbarbutton when hover*/
    
    background-color: #4d4d4d !important;
}
.tg_head_split {
    background-color: #151515;
}
.im_submit {
    color: #4d4d4d;
}
.im_message_mymention {
    background: #1d1d1d;
}
.im_message_focus .im_message_outer_wrap {
    background-color: #4d4d4d;
}
.im_message_selected .im_message_outer_wrap {
    background: #4d4d4d;
}
a:hover {
    background-color: #282828 !important;
}
.im_message_unread_split {
    background: #4d4d4d;
}
@keyframes im_message_focus_fade {
    0% {
        /*soft transaction*/
        
        background-color: #2d2d2d;
    }
}
.btn-primary {
    background-color: #4d4d4d;
}
.im_history_select_active .im_message_outer_wrap:hover {
    background: #4d4d4d;
}
.im_dialog_chat_from_wrap,
.im_dialog_message_service {
    color: #6ac;
}
.composer_rich_textarea,
.composer_textarea {
    box-shadow: 0px 3px 1px #ddd;
}
.composer_rich_textarea:focus {
    box-shadow: 0px 3px 1px #ddd !important;
}
.tg_head_logo_dropdown .dropdown-menu > li > a {
    color: #ddd;
}
.btn-primary.disabled,
.btn-primary[disabled] {
    background: #4d4d4d none repeat scroll 0% 0%;
}
/*emoji*/

.composer_emoji_tooltip,
.icon-tooltip-tail,
.dropdown-menu {
    background-color: #4d4d4d !important;
    border-color: #5d5d5d;
}

@namespace url(http://www.w3.org/1999/xhtml);
* {
background: #262626 !important;
border-color: #262626 !important;
color: #9a9a9a !important;
}

#p-logo a {
background-position:center center !important;
background-repeat:no-repeat !important;
}


a{
color: #000000 !important;
}

a:visited{
color: #ccc !important;
}

a:link{
color: #ccc !important;
font-weight: bold !important;
}

a:hover, a:hover span {
color: #fff !important;
text-decoration: underline !important;
}

#content, #content * {
background: #202020 !important;
border-color: #555 !important;
}

#content {
-moz-border-radius: 10px;
-webkit-border-radius: 10px;
border-radius: 10px;
}

#toc, #toc * {
background: #262626 !important;
border-color: #262626 !important;
}

#toc {
-moz-border-radius: 10px;
-webkit-border-radius: 10px;
border-radius: 10px;
}

#p-cactions li.selected a, #p-cactions li a, #p-cactions li a:hover {
padding: 0 1em 0.1em !important;
}

#bodyContent pre {
background: #1a1a1a !important;
}

