#viewport, #gsr {
    background: #F5F5F5;
}
a:hover {
    text-decoration: none !important;
}
.g a, ._dTj, ._MLo, ._NLo, #center_col ._Ak a._kBb {
    color: #0D47A1 !important;
}
.s, .s .st em, .st.s.std em, ._AOf, .st, .kno-fb-ctx, ._xdb .fl, #center_col ._Ak, .ads-creative b, body, ._W5e a, a._Zjg, .kno-ftr, a.duf3 {
	color: #545454 !important;
}
._IId, .a, cite, cite a:link, cite a:visited, .cite, .cite:link, #_bGc>i, .bc a:link {
	color: #00695C !important;
}
#logo {
    display: inline;
    position: relative;
    top: -3px;
}
.hp #gb {
    position: absolute !important;
    top: -290px !important;
}
.gb_kb, .gb_lb, .gb_7d.gb_R, #prm {
    display: none !important;
}
.gb_Jc .gb_b {
    width: 35px;
    height: 35px;
    background-size: 30px !important;
    background-position: 3px 3px;
}
.gb_rc .gb_vc {
    width: 25px;
    height: 25px;
    padding: 0;
    margin: 0;
    background-color: rgba(0,0,0,0.55) !important;
}
.gb_b.gb_4b {
    background: url('data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAGAAAABgAQMAAADYVuV7AAAABlBMVEUAAAAAAAClZ7nPAAAAAXRSTlMAQObYZgAAABpJREFUeAFjGH7g/38IGg4ceoLRcBsNt2EHAG/VHvBf4t10AAAAAElFTkSuQmCC') no-repeat;
    background-position: -2px -3px !important;
    background-size: 30px;
}
.gb_Fc.gb_Ec {
    width: 25px;
    height: 25px;
    margin: 0;
    background: url('data:image/png;base64,') no-repeat;
    background-color: rgba(0,0,0,.55);
    background-position: 5px 5px !important;
    background-size: 15px !important;
    z-index: 10;
}
.gbii {
    position: relative;
    top: -6px;
    background-repeat: no-repeat;
    background-size: 40px 40px;
    width: 40px;
    height: 40px;
}
.gbii:before {
    display: none;
}
.gb_ha, .gb_ga {
    border: 0;
    border-radius: 3px;
}
.gb_Ha, #gbsfw.gb_yc, .gb_wb.gb_Me {
    border: 0;
    border-radius: 2px;
    background: #FFF;
    box-shadow: 0 1px 2px 0 rgba(0,0,0,0.2);
    transition: box-shadow 0.2s;
    cursor: pointer !important;
}
.gb_O {
	padding: 2px 0 5px 0;
}
.gb_Z[aria-grabbed="true"] {
	z-index: 1010;
}
.gb_Z:hover .gb_O {
    border: 1px solid transparent !important;
    background: #EEE !important;
}
.gb_Z:hover .gb_4 {
    background: #EEE !important;
}
